"use strict";

var _steps = require("cypress-cucumber-preprocessor/steps");

var _index = require("../common/index");

(0, _steps.Given)('the custom title is {string} and the app title is "Example!"', applicationTitle => {
  cy.get('@applicationTitleFixture').then(fx => {
    cy.route({
      url: `${_index.baseUrl}api/systemSettings/applicationTitle`,
      response: { ...fx,
        applicationTitle
      }
    }).as('applicationTitle');
  });
});
(0, _steps.Then)('the displayed title should be "Barbaz - Example!"', () => {
  cy.get('[data-test="headerbar-title"]').should($title => {
    expect($title.text()).to.equal('Barbaz - Example!');
  });
});