"use strict";

var _textIcon = require("./text-icon.js");

describe('TextIcon - getInitials', () => {
  it('should return initials when given a name', () => {
    const actual = (0, _textIcon.getInitials)('Some Name');
    const expected = 'SN';
    expect(actual).toBe(expected);
  });
  it('should handle single names', () => {
    const actual = (0, _textIcon.getInitials)('Name');
    const expected = 'N';
    expect(actual).toBe(expected);
  });
  it('should only return the first two initials', () => {
    const actual = (0, _textIcon.getInitials)('A Very Long Name');
    const expected = 'AV';
    expect(actual).toBe(expected);
  });
  it('should handle utf-8 multi byte characters', () => {
    const actual = (0, _textIcon.getInitials)('恨恨 한한한');
    const expected = '恨한';
    expect(actual).toBe(expected);
  });
});