"use strict";

var _sharingConstants = require("../sharing-constants.js");

describe('SharingDialog - convertAccessToConstant', () => {
  it('returns the default value for public sharing if the access string is not recognised', () => {
    expect((0, _sharingConstants.convertAccessToConstant)()).toEqual(_sharingConstants.defaultSharingSettings.public);
    expect((0, _sharingConstants.convertAccessToConstant)('invalid-access-string')).toEqual(_sharingConstants.defaultSharingSettings.public);
  });
  it('parses the metadata portion of the access string correctly', () => {
    const testCases = [['--------', _sharingConstants.ACCESS_NONE], ['r-------', _sharingConstants.ACCESS_VIEW_ONLY], ['r-r-----', _sharingConstants.ACCESS_VIEW_ONLY], ['rw------', _sharingConstants.ACCESS_VIEW_AND_EDIT], ['rwrw----', _sharingConstants.ACCESS_VIEW_AND_EDIT]];
    testCases.forEach(([accessString, accessConstant]) => expect((0, _sharingConstants.convertAccessToConstant)(accessString)).toEqual(accessConstant));
  });
});