"use strict";

var _sharingConstants = require("../sharing-constants.js");

describe('SharingDialog - convertConstantToAccess', () => {
  const testCases = [[_sharingConstants.ACCESS_NONE, '--------', false], [_sharingConstants.ACCESS_VIEW_ONLY, 'r-------', true], [_sharingConstants.ACCESS_VIEW_AND_EDIT, 'rw------', true]];
  it('returns the default access string if the access constant is not recognised', () => expect((0, _sharingConstants.convertConstantToAccess)('NOT_RECOGNISED')).toEqual('--------'));
  it('returns the correct metadata access string', () => testCases.forEach(([accessConstant, accessString]) => expect((0, _sharingConstants.convertConstantToAccess)(accessConstant)).toEqual(accessString)));
  it('returns the correct boolean value', () => testCases.forEach(testCase => expect((0, _sharingConstants.convertConstantToAccess)(testCase[0], true)).toEqual(testCase[2])));
});